          var scrollPercent = scroll / (w - h) * 10
          
          document.getElementById("bullet").style.top = scrollPercent+"px"
        })

        this.startAnimations();
        
    },

    beforeDestroy() {
        this.lmS.destroy();
    },

    methods: {
        startAnimations: function(){
          //document.getElementById("bullet").style.top = 0+"px"

          let data = sessionStorage.getItem('Index');

          if(data == '1'){
            this.gsap.to(".loading", .2, { display: 'none'})
            this.gsap.from("#bullet", .6, {opacity: 0, ease: "power1.out"}).delay(.1)
            this.gsap.from("#bullet", .8, {y: -24, ease: "power2.out"}).delay(.5)
            this.gsap.to(".hero-legend", .5, { opacity: 1, y: 0 }).delay(.1)

            let mark = document.querySelectorAll(".mark")
            for (let index = 0; index < mark.length; index++) {
              mark[index].removeAttribute("class")
            }
            
          }else{
            this.gsap.to(".loading", .5, { display: 'none'}).delay(0.2)
            this.gsap.from(".logo", .5, { opacity: 0, x: "-32px" }).delay(4.7)
            this.gsap.from(".nav-item", .5, { opacity: 0, x: '32px' }).delay(4.7)
            this.gsap.from(".mobile-menu", .5, { opacity: 0, x: '32px' }).delay(4.7)
            this.gsap.to(".mark-p1", .6, { "stroke-dashoffset": 0, ease: "power1.in" }).delay(0.5)
            this.gsap.to(".mark-p2", .7, { "stroke-dashoffset": 0, ease: "power1.in" }).delay(0.8)
            this.gsap.to(".mark-z", 2.9, { "stroke-dashoffset": 0, ease: "power1.in" }).delay(1.3)
            this.gsap.to(".mark-b1", .4, { "stroke-dashoffset": 0, ease: "power2.out" }).delay(2.0)
            this.gsap.to(".mark-in", 1.2, { "stroke-dashoffset": 0, ease: "power2.out" }).delay(3.7)
            this.gsap.to(".mark-t", .5, { "stroke-dashoffset": 0, ease: "power1.out" }).delay(3.55)
            this.gsap.to(".mark-b2", .4, { "stroke-dashoffset": 0, ease: "power2.out" }).delay(4.1)
            this.gsap.to(".hero-legend", .5, { opacity: 1, y: 0 }).delay(4.5)

            this.gsap.from("#liner", .7, {height: 0, y: 60, ease: "power4.out"}).delay(4.7)
            this.gsap.from("#bullet", .6, {opacity: 0, y: -24, ease: "power1.out"}).delay(5.2)
            this.gsap.to("#bullet", .8, {y: 0, ease: "power2.out"}).delay(5.7)
            this.gsap.from("#progressbar-text", .8, {y: 12, opacity: 0, ease: "power1.out"}).delay(6)

            let IndexStore = sessionStorage.setItem('Index', '1');
          }

        },

        serviceOver: function(event){
          var img, i;
          img = document.querySelectorAll(".sector-list");
          //console.log(img)
          for (i = 0; i < img.length; i++) {
              img[i].classList.remove("section-list__image--active");
          }
          let link = document.getElementById(event.target.id).parentNode.classList.add("section-list__image--active")

        },

        serviceOut: function(event){
          var img, i;
          img = document.querySelectorAll(".sector-list");
          //console.log(img)
          for (i = 0; i < img.length; i++) {
              img[i].classList.remove("section-list__image--active");
          }
        }
    }
    
  }
</script>